sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || newValue == oldValue) {
      return;
   }

   var nameAjax = new GlideAjax('GetFirstnLastName');


   nameAjax.addParam('sysparm_name', 'getFnLName');
   nameAjax.addParam('sysparm_userID', g_form.getValue('u_requested_for'));

   nameAjax.getXML(GetFnLName);

   function GetFnLName(response){
      var userObj = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));

      g_form.clearValue('u_requested_for_first_name');
      g_form.clearValue('u_requested_for_last_name');
      g_form.setValue('u_requested_for_first_name', userObj.f_name +"");
      g_form.setValue('u_requested_for_last_name', userObj.l_name+"");
   }

}
