var GetFirstnLastName = Class.create();
GetFirstnLastName.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getFnLName: function(){
        var userObj = {};
        var grUser = new GlideRecord('sys_user');

        grUser.get(this.getParameter('sysparm_userID'));
        userObj.f_name = grUser.first_name+"";
        userObj.l_name = grUser.last_name+"";

		var myObj = JSON.stringify(userObj);

        return myObj;
    },

    type: 'GetFirstnLastName'
});
